mapper.select:
SELECT left(gugun_code,5) gugun_code, gugun_name FROM guguncode
where left(gugun_code,2) = #{sido}
ORDER BY gugun_code

SELECT distinct dong, code FROM houseinfo
where code = #{gugun}
ORDER BY dong

SELECT no,dong,AptName,dealAmount,code,jibun FROM housedeal where dong = #{dong}
order by dealAmount desc
limit 10
